import React, { useState } from 'react'
import './contact.scss'
export default function Contact() {
    const [message, setMessage] = useState(false)
    const [change, setChange] = useState('')

    const onChange = (e) => {
        setChange({
            ...change,
            [e.target.name]: e.target.value
        })
    }
    const handleSubmit = (e) => {
        setMessage(true)
        e.preventDefault()
    }
    return (
        <div className='contact' id='contact'>
            <div className="left">
                <img src="assets/shake.svg" alt="" />
            </div>
            <div className="right">
                <h2>C O N T A C T</h2>
                <form onSubmit={handleSubmit} >
                    <input type="email" placeholder="Email" onChange={onChange} name="email"/>
                    <textarea placeholder="Message" onChange={onChange}> </textarea>
                    <button type="submit">Send</button>
                    {message && <span>Thanks for connecting, I'll be in touch within 24-48 business hours.</span>}
                </form>
            </div>
        </div>
    )
}
