import React, { useEffect, useState } from 'react'
import PortfolioList from '../portfolioList/PortfolioList'
import './portfolio.scss'
import {CRYPTO, TVSHOW, Plants, Bubble, featuredPortfolio} from '../../data'


export default function Portfolio() {
    const [selected, setSelected] = useState('featured')
    const [data, setData] = useState([])
    const list = [
        {
            id: 'featured',
            title: 'Featured'
        },
        {
            id: 'crypto',
            title: 'Crypto Tracker'
        },
        {
            id: 'tvShowSelector',
            title: 'TV Show Selector'
        },
        {
            id: 'reactPlants',
            title: 'React Plants Ecommerce'
        },
        {
            id: 'bubblePicker',
            title: 'Bubble Color Picker'
        }
    ]

    useEffect(() => {

        switch(selected){
            case 'featured': 
            setData(featuredPortfolio);
            break;
            case 'crypto': 
            setData(CRYPTO);
            break;
            case 'tvShowSelector': 
            setData(TVSHOW);
            break;
            case 'reactPlants': 
            setData(Plants);
            break;
            case 'bubblePicker': 
            setData(Bubble);
            break;
            default:
                setData(featuredPortfolio)
        }

    }, [selected])
    return (
        <div className='portfolio' id='portfolio'>
            <h1>Portfolio</h1>
            <ul>
                {list.map(item => (
                    <PortfolioList title={item.title} active={selected === item.id} setSelected={setSelected} id={item.id}/>
                ))}
            </ul>
            <div className="container">
                {data.map(d => (
                <div className="item">
                <img src={d.img} alt=''/>
                <h3>{d.title}</h3>
                    </div>
                ))}
            </div>
        </div>
    )
}
